import React from 'react'
import { Component } from 'react'
import Node from './Node.jsx'
import './grid.css'
import {getAllNodes, getNodesInShortestPathOrder} from './Algorithms/AlgoTools'
import {dijkstra} from './Algorithms/Dijkstras';
import {aStar} from './Algorithms/Astar';
import {generateWall1,generateWall2,generateWall3} from './obstacles/RandomWalls'
import {createMaze} from './obstacles/Maze'
import {unweightedSearchAlgorithm} from './Algorithms/BFS';
import {dfs} from './Algorithms/DFS';
import {Table} from 'react-bootstrap';

export default class Grid extends Component
{
  constructor() 
    {
        super();
        this.state = {
          grid: [],
          mouseIsPressed: false,
          currentAlgorithm: [],
          performance:[],
        };
    } 
   
   
  componentDidMount() {
    const grid = getInitialGrid();
    getAllNodes(grid)
    this.setState({grid});
  }
    
    
  handleMouseDown(row, col) {
    const newGrid = getNewGridWithWallToggled(this.state.grid, row, col);
    this.setState({grid: newGrid, mouseIsPressed: true});
  }

  handleMouseEnter(row, col) {
    if (!this.state.mouseIsPressed) return;
    const newGrid = getNewGridWithWallToggled(this.state.grid, row, col);
    this.setState({grid: newGrid});
  }

  handleMouseUp() {
    this.setState({mouseIsPressed: false});
  }
  clearGrid(){ 
    const {grid}=this.state    
    for(var i=0;i<grid.length;i++){
      for(var j=0;j<grid[i].length;j++){
            
        grid[i][j].isVisited=false
        if((!grid[i][j].isStart)&&(!grid[i][j].isFinish)&&(!grid[i][j].isWall))
        document.getElementById(`node-${grid[i][j].row}-${grid[i][j].col}`).className =
        'node ';
      }
    }
  }

  recordPerofrmence(algo,time,numberOfVisited,lengthOfSHortestPath){
    const {performance}=this.state;
    var tempPerformance = new Object();
    tempPerformance.algorithm=algo;
    tempPerformance.time=time;
    tempPerformance.shortestPathLength=lengthOfSHortestPath;
    tempPerformance.numberOfVisitedNodes=numberOfVisited;
    performance.push(tempPerformance);
    console.log('performance');
    console.log(performance);
  }

  displayPerformance(){
    const {performance} = this.state;
    const {currentAlgorithm}= this.state
    console.log('hu na bhai please');
    console.log(currentAlgorithm)
    console.log(currentAlgorithm.length);
    let time = performance[0].time.toString();
    let algorithm = performance[0].algorithm;
    let distance = performance[0].shortestPathLength.toString();
    document.getElementById('algorithm').innerHTML= 'algorithm: ' + algorithm ;
    document.getElementById('time').innerHTML= 'time: ' + time;
    document.getElementById('distanceTravelled').innerHTML = 'distance travelled: ' + distance;
    // for(let i=0;i<2;i++){

    //   let time = performance[i].time.toString();
    //   document.getElementById('time').innerHTML=time[i];

    // }
  }
 
  animateAlgorithm(visitedNodesInOrder, nodesInShortestPathOrder) {
    
    for (let i=0; i <= visitedNodesInOrder.length; i++) {
      if (i === visitedNodesInOrder.length) {
        setTimeout(() => {
          this.animateShortestPath(nodesInShortestPathOrder);
        }, 20 * i);
        return;
      }
      setTimeout(() => {
        const node = visitedNodesInOrder[i];
        if(!(visitedNodesInOrder[i].isStart||visitedNodesInOrder[i].isFinish))
        {
        document.getElementById(`node-${node.row}-${node.col}`).className =
          'node node-visited';
        }
      }, 20 * i);
    }
  }

  animateShortestPath(nodesInShortestPathOrder) {
    let j = 0
    for (let i=0; i < nodesInShortestPathOrder.length; i++) 
    {
      setTimeout(() => {
        const node = nodesInShortestPathOrder[i];
        if(!(nodesInShortestPathOrder[i].isStart||nodesInShortestPathOrder[i].isFinish))
        {
        document.getElementById(`node-${node.row}-${node.col}`).className =
          'node node-shortest-path';
        } 
      }, 30 * i);
      j++
    }
    setTimeout(() => {
     this.clearGrid()
    }, 20 * j*2);
    setTimeout(() => {
      this.startVisualization()
        }, 20 * j*2);
    
  }

  visualizeDijkstra() {
   
    const {grid} = this.state;
    const startNode = grid[10][15];
    const finishNode = grid[10][35];
    var t0=performance.now()
    const visitedNodesInOrder = dijkstra(grid, startNode, finishNode);
    var t1=performance.now()
    const nodesInShortestPathOrder = getNodesInShortestPathOrder(finishNode);
    console.log(nodesInShortestPathOrder)
    this.animateAlgorithm(visitedNodesInOrder, nodesInShortestPathOrder);
    this.recordPerofrmence('dijkstras',t1-t0,visitedNodesInOrder.length,nodesInShortestPathOrder.length)
  }
  visualizeAstar() {
   
    const {grid} = this.state;
    const startNode = grid[10][15];
    const finishNode = grid[10][35];
    var t0=performance.now()
    const visitedNodesInOrder = aStar(grid, startNode, finishNode);
    var t1=performance.now()
    console.log(visitedNodesInOrder)
    const nodesInShortestPathOrder = getNodesInShortestPathOrder(finishNode);
    console.log(nodesInShortestPathOrder)
    this.animateAlgorithm(visitedNodesInOrder, nodesInShortestPathOrder);
    this.recordPerofrmence('astar',t1-t0,visitedNodesInOrder.length,nodesInShortestPathOrder.length)
  }


  visualizeBfs() {
    
    const {grid} = this.state;
    const startNode = grid[10][15];
    const finishNode = grid[10][35];
    var t0=performance.now()
    const visitedNodesInOrder =  unweightedSearchAlgorithm(grid, startNode, finishNode,'bfs');
    var t1=performance.now()
    const nodesInShortestPathOrder = getNodesInShortestPathOrder(finishNode);
    console.log(nodesInShortestPathOrder)
    this.animateAlgorithm(visitedNodesInOrder, nodesInShortestPathOrder);  
    this.recordPerofrmence('bfs',t1-t0,visitedNodesInOrder.length,nodesInShortestPathOrder.length)
  }

  visualizeDfs() {
    const {grid} = this.state;
    const startNode = grid[10][15];
    const finishNode = grid[10][35];
    var t0=performance.now()
    const visitedNodesInOrder =  dfs(grid, startNode, finishNode);
    var t1=performance.now()
    const nodesInShortestPathOrder = getNodesInShortestPathOrder(finishNode);
    console.log(nodesInShortestPathOrder)
    this.animateAlgorithm(visitedNodesInOrder, nodesInShortestPathOrder);  
    this.recordPerofrmence('dfs',t1-t0,visitedNodesInOrder.length,nodesInShortestPathOrder.length)
  }

  selectionfunction(algo){
    const {currentAlgorithm}= this.state
    if(!currentAlgorithm.includes(algo)){
    currentAlgorithm.push(algo)
   }
    console.log(currentAlgorithm)
  }

  startVisualization(){
      const {currentAlgorithm}= this.state
      console.log('hu na bhai');
      console.log(currentAlgorithm)
      console.log(currentAlgorithm.length);
      var algo=currentAlgorithm.pop()
    
      if(algo=='dijsktras'){
        console.log('dijkstras')
        this.visualizeDijkstra()
        // setTimeout(() => {
        //   this.clearGrid()
        // },50 * 3);
        
      }
      if(algo=='astar'){
        console.log('astar')
        this.visualizeAstar()
        // setTimeout(() => {
        //   this.clearGrid()
        // },50 * 3);
      }
      if(algo=='bfs'){
        console.log('bfs')
        this.visualizeBfs()
        // setTimeout(() => {
        //   this.clearGrid()
        // },50 * 3);
      }

      if(algo=='dfs'){
        console.log('dfs')
        this.visualizeDfs()
        // setTimeout(() => {
        //   this.clearGrid()
        // },50 * 3);
      }

  }

  render() {
    
    const {grid, mouseIsPressed} = this.state;
    
    return (
      <>
      <div className="navBar">
        <a href="http://localhost:3000/">  <b> Pathfinding Visualizer </b></a>
        <a>  <button onClick={()=>this.startVisualization()} className="newTools"> <b>Start Visualization </b> </button></a>
        <a>  <button onClick={()=>this.clearGrid()} className="newTools"> <b>Clear Grid </b> </button></a>
        <a>  <button onClick={()=>this.displayPerformance()} className="newTools"> <b> performance </b> </button></a>
        {/* <div className="dropDown"> 
          <a className="dropBtn"><b> Algorithms </b></a>
          <div className="dropdown-algo">
          <a onClick={() => this.visualizeDijkstra()}> <a> Dijkstra's algorithm</a> </a>
          <a onClick={() => this.visualizeAstar()}> <a> Astar algorithm</a> </a>
          <a onClick={() => this.visualizeBfs()}> <a> Bfs algorithm</a> </a>
          <a onClick={() => this.visualizeDfs()}> <a> Dfs algorithm</a> </a>
          </div> 
        </div> 
     */}

	      <div className="dropDown"> 
          <label className="dropBtn"><b> Algorithms </b></label>
          
          
          <select className="dropdown-algo" id="dropdown-algo" multiple>
          <option onClick={()=>this.selectionfunction('dijsktras')} id='dijsktras' className="algoBar"> Dijkstra's algorithm </option> 
          <option  onClick={()=>this.selectionfunction('astar')} id='astar' className="algoBar">  Astar algorithm  </option>
          <option  onClick={()=>this.selectionfunction('bfs') }id='bfs' className="algoBar">  Bfs algorithm  </option>
          <option  onClick={()=>this.selectionfunction('dfs')} id='dfs'className="algoBar" >  Dfs algorithm   </option>
          </select> 
        </div>



      
        <div className="wall"> 
          <a className="dropWall"><b> Wall </b> </a>
          <div className="dropdown-wall">
            <a onClick=
                        {() => 
                              { const {grid} = this.state;
                                const newGrid=generateWall1(grid)
                                this.setState({grid: newGrid})
                              }
                        }
            >  <a> Level 1</a> </a> 
           <a onClick=
                        {() => 
                              {
                                const {grid} = this.state;
                                const newGrid=generateWall2(grid)
                                this.setState({grid: newGrid})
                              }
                        }
            >  <a> Level 2</a> </a> 
           <a onClick=
                        {() => 
                              {
                                const {grid} = this.state;
                                const newGrid=generateWall3(grid)
                                this.setState({grid: newGrid})
                              }
                        }
            >  <a> Level 3</a> </a> 
            <a onClick=
              {()=>
                  {const {grid} = this.state;
                  const newGrid=createMaze(grid)
                  this.setState({grid: newGrid})
                  
                  }
              }
            >  <a> Generate Maze</a> </a>
          </div> 
        </div>
       <a href="http://localhost:3000/"> <b> Reset</b></a>
      </div>

      <div id= 'algorithm'></div>
      <div id= 'time'></div>
      <div id= 'distanceTravelled'></div>
      
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>#</th>
            <th>ALgorithm</th>
            <th>Time</th>
            <th>Shortest Path Length</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>1</td>
            <td id='name0'></td>
            <td id='name1'></td>
            <td id='name2'></td>
            <td id='name3'></td>
          </tr>
          <tr>
            <td>2</td>
            <td>Jacob</td>
            <td>Thornton</td>
            <td>@fat</td>
          </tr>
          <tr>
            <td>3</td>
            <td colSpan="2">Larry the Bird</td>
            <td>@twitter</td>
          </tr>
        </tbody>
      </Table>

      <div className="grid"> 
        {grid.map((row, rowIdx) => { 
          return (
            <div key={rowIdx}>
              {row.map((node, nodeIdx) => {
                const {row, col, isFinish, isStart, isWall} = node;
                return (
                  <Node
                    key={nodeIdx}
                    col={col}
                    isFinish={isFinish}
                    isStart={isStart}
                    isWall={isWall}
                    mouseIsPressed={mouseIsPressed}
                    onMouseDown={(row, col) => this.handleMouseDown(row, col)}
                    onMouseEnter={(row, col) =>
                      this.handleMouseEnter(row, col)
                    }
                    onMouseUp={() => this.handleMouseUp()}
                    row={row}></Node>
                );
              })}
            </div>
          );
        })}
      </div>
    </> 
    );     
  }
};

const getInitialGrid = () => {
  const grid = []
  for (let row = 0; row < 20; row++) {
    const temp = [];
    for (let col = 0; col < 50; col++) {
      temp.push(createNode(col, row));
    }
    grid.push(temp);
  }
  return grid;
};
const createNode = (col, row) => {
  return {
    col,
    row,
    isStart: row === 10 && col === 15,
    isFinish: row === 10&& col === 35,
    distance: Infinity,
    isVisited: false,
    isWall: false,
    previousNode: null,
  };
};

const getNewGridWithWallToggled = (grid, row, col) => {
  const newGrid = grid.slice();
  const node = newGrid[row][col];
  const newNode = {
    ...node,
    isWall: !node.isWall,
  };
  newGrid[row][col] = newNode;
  return newGrid;
};
function sleep(milliseconds) {
  var start = new Date().getTime();
  for (var i = 0; i < 1e7; i++) {
    if ((new Date().getTime() - start) > milliseconds){
      break;
    }
  }
}
