export function unweightedSearchAlgorithm(grid, startNode, finishNode) {
  if (!startNode || !finishNode || startNode === finishNode) {
    return false;
  }
  let structure = startNode;
  let exploredNodes = {startNode: true};
  while (structure.length) {
    let currentNode = name === "bfs" ? structure.shift() : structure.pop();
    queue.push(currentNode);
    if (name === "dfs") exploredNodes[currentNode.id] = true;
    currentNode.isVisited = true;
    if (currentNode.id === finishNode) {
      return queue;
    }
    let currentNeighbors = getNeighbors(currentNode.id, boardArray, name);
    currentNeighbors.forEach(neighbor => {
      if (!exploredNodes[neighbor]) {
        if (name === "bfs") exploredNodes[neighbor] = true;
        neighbor.previousNode = currentNode.id;
        structure.push(neighbor);
      }
    });
  }
  return false;
}

