  const {col, row} = node;

  if (row > 0) {
    if (name === "bfs") {
      neighbors.push(grid[row - 1][col]);
    } else {
      neighbors.unshift(grid[row - 1][col]);
    }
  }

  if (row > grid.length - 1) {
    if (name === "bfs") {
      neighbors.push(grid[row][col + 1]);
    } else {
      neighbors.unshift(grid[row][col + 1]);
    }
  } 

  if (row < grid.length - 1) {
    if (name === "bfs") {
      neighbors.push(grid[row + 1][col]);
    } else {
      neighbors.unshift(grid[row + 1][col]);
    }
  } 

  if (col > 0) {
    if (name === "bfs") {
      neighbors.push(grid[row][col - 1]);
    } else {
      neighbors.unshift(grid[row][col - 1]);
    }
  } 
  return neighbors.filter(neighbor => !neighbor.isVisited);
}
