  }
  
  if (
    row >= 0 &&
    row < ROWS &&
    col + 1 >= 0 &&
    col + 1 < COLS &&
    !grid[row][col + 1].isWall &&
    !grid[row][col + 1].isVisited
  ) {
    neighbors.unshift(grid[row][col + 1]);
  }

  if (
      row + 1 >= 0 &&
      row + 1 < ROWS &&
      col >= 0 &&
      col < COLS &&
      !grid[row + 1][col].isVisited &&
      !grid[row + 1][col].isWall
  ) {
      neighbors.unshift(grid[row + 1][col]);
  }
 
  if (
      row >= 0 &&
      row < ROWS &&
      col - 1 >= 0 &&
      col - 1 < COLS &&
      !grid[row][col - 1].isWall &&
      !grid[row][col - 1].isVisited
  ) {
      neighbors.unshift(grid[row][col - 1]);
  }
  

  return neighbors;
}
