import React from 'react'
import { Component } from 'react'
import Node from './Node'
import './grid.css'

export default class Grid extends Component
{
    constructor() 
    {
        super();
        this.state = {
          grid: [],
          mouseIsPressed: false,
        };
    } 
    componentDidMount() {
      const grid = this.getInitialGrid();
      this.setState({grid});
    }
    // getInitialGrid = () => {
    //   const grid = []
    //   for (let row = 0; row < 20; row++) {
    //     const temp = [];
    //     for (let col = 0; col < 50; col++) {
    //       temp[col]=this.createNode(col,row)
    //     }
    //     grid[row]=temp
    //   }
    //   return grid;
    // };
    // createNode = (col, row) => {
    //   return {
    //     col,
    //     row,
    //     isStart: false,
    //     isFinish:  false,
    //     //distance: Infinity,
    //     //isVisited: false,
    //     isWall: false,
    //     //previousNode: null,
    //   };
    // };
    

    render() {
        const {grid, mouseIsPressed} = this.state;
        
    
        return (
          <>
            <button onClick={() => this.visualizeDijkstra()}>
              Visualize Dijkstra's Algorithm(currently not working)
            </button>
            <div className="grid"> 
              {grid.map((row, rowIdx) => { 
                return (
                  <div key={rowIdx}>
                    {row.map((node, nodeIdx) => {
                      const {row, col, isFinish, isStart, isWall} = node;
                      return (
                        <Node
                          key={nodeIdx}
                          col={col}
                          isFinish={isFinish}
                          isStart={isStart}
                          isWall={isWall}
                          mouseIsPressed={mouseIsPressed}
                          onMouseDown={(row, col) => this.handleMouseDown(row, col)}
                          onMouseEnter={(row, col) =>
                            this.handleMouseEnter(row, col)
                          }
                          onMouseUp={() => this.handleMouseUp()}
                          row={row}></Node>
                      );
                    })}
                  </div>
                );
              })}
            </div>
          </>
        );
      }
};

getInitialGrid = () => {
  const grid = []
  for (let row = 0; row < 20; row++) {
    const temp = [];
    for (let col = 0; col < 50; col++) {
      temp[col]=this.createNode(col,row)
    }
    grid[row]=temp
  }
  return grid;
};
var createNode = function(col, row) {
  return {
    col,
    row,
    isStart: row === 10 && col === 15,
    isFinish: row === 10 && col === 35,
    //distance: Infinity,
    //isVisited: false,
    isWall: false,
    //previousNode: null,
  };
};
